import { useEffect, useState } from "react"
import { useParams } from "react-router"
import ServiceListeProduits from "../services/ProduitsAPI"
import { Link } from "react-router-dom"

const Categorie = () => {

    let { libelleCategorie } = useParams();


    const [produits, setProduits] = useState([])

    const fetchAll = async () => {
        const _produits = await ServiceListeProduits.getProduitsParCategorie(libelleCategorie)
        setProduits(_produits)
    }

    useEffect(() => {
        fetchAll()
    }, [])

    return (
        <>
            
            <h1>{libelleCategorie}</h1>
            <ul className="list-unstyled">
                {
                    produits.map(produit => {
                        return (<li key={produit.idProduit}><Link to={`/produits/${produit.idProduit}`} role="button">{produit.libelleProduit}</Link></li>)
                    })
                }
            </ul> 
        </>
    )
}


export default { Categorie }
