import axiosInstance from "../config/axios"


const getProduits = () => {

    return axiosInstance
        .get('/produits')
        .then(response => response.data)
}

const getProduitParID = (id) => {

    return axiosInstance
    .get(`/produits/${id}`)
    .then(response => response.data)
}

const getProduitParCategorie = () => {
    
    return axiosInstance
    .get
}


export default { getProduits, getProduitParID }
