import { useEffect, useState } from "react"
import { useParams } from "react-router"
import ServiceListeProduits from "../services/ProduitsAPI"
import { Link } from "react-router-dom"

const Categorie = () => {

    let { idCategorie } = useParams();


    const [produits, setProduits] = useState([])
    const [categorie, setCategories] = useState([])

    const fetchAll = async () => {
        const _produits = await ServiceListeProduits.getProduitsParCategorie(idCategorie)
        setProduits(_produits)
    }

    useEffect(() => {
        fetchAll()
    }, [])

    return (
        <>
            {
            produits.length > 0 ? 
            (
                <div>
                    <h1>{idCategorie}</h1>
                    <ul className="list-unstyled">
                        {
                            produits.map(produit => {
                                return (<li key={produit.idProduit}><Link to={`/produit/${produit.idProduit}`} role="button">{produit.libelleProduit}</Link></li>)
                            })
                        }
                    </ul>
                </div>
            ) : 
            (
            <h1>La cat√©gorie {idCategorie} ne comporte aucun produit</h1>
            )}
        </>
    )
}


export default Categorie
