import { useEffect, useState } from "react"
import { useParams } from "react-router"
import ServiceListeProduits from "../services/ProduitsAPI"
import { Link } from "react-router-dom"

const CategorieCafe = () => {
    
    let { idCategorie } = useParams();
    console.log(idCategorie)

    if (idCategorie === "cafe") {
        idCategorie = 2
    }
    
    if (idCategorie === "the") {
        idCategorie = 2
    }

    const [produits, setProduits] = useState([])

    const fetchAll = async () => {
        const _produits = await ServiceListeProduits.getProduitsParCategorie(idCategorie)
        setProduits(_produits)
    }

    useEffect(() => {
        fetchAll()
    }, [])

    return(
        <>
            <h1>Café</h1>
            <ol className="breadcrumb">
                <li className="breadcrumb-item"><a href="/">Accueil</a></li>
                <li className="breadcrumb-item"><a href="/categorie">Catégorie</a></li>
                <li className="breadcrumb-item active">Café</li>
            </ol>
            <ul className="list-unstyled">
                {
                    produits.map(produit => {
                        return (<li key={produit.idProduit}><Link to={`/produits/${produit.idProduit}`} role="button">{produit.libelleProduit}</Link></li>)
                    })
                }
            </ul>
        </>
    )
}

export default CategorieCafe
