import { useEffect, useState } from "react"
import ServiceListeArticles from "../services/ArticlesAPI"
import { Link } from "react-router-dom"

const Rubriques = () => {

    const [rubriques, setRubriques] = useState([])

    const fetchAll = async () => {
        const _rubriques = await ServiceListeArticles.getRubriques()
        setRubriques(_rubriques)
    }

    useEffect(() => {
        fetchAll()
    }, [])

    return(
        <>
            <h1>
                Rubriques :
            </h1>
            <ul className="sans-style">
                {
                    rubriques.map(rubrique => {
                        return (<li key={rubrique.$idRubrique}><Link to={`/rubrique/${rubrique.titre}`} role="button">{rubrique.titre}</Link></li>)
                    })
                }
            </ul>
        </>
    )
}

export default Rubriques
