import { useEffect, useState } from "react"
import ServiceListeProduits from "../services/CatrogiesAPI"
import { Link } from "react-router-dom"

const Categories = () => {

    const [categories, setCategories] = useState([])

    const fetchAll = async () => {
        const _categories = await ServiceListeProduits.getCategories()
        setCategories(_categories)
    }

    useEffect(() => {
        fetchAll()
    }, [])

    return (
        <>
            
            <h1>Liste des cat√©gories :</h1>
            <ul className="list-unstyled">
                {
                    categories.map(categorie => {
                        return (<li key={categorie.$idCategorie}><Link to={`/categorie/${categorie.libelleCategorie}`} role="button">{categorie.libelleCategorie}</Link></li>)
                    })
                }
            </ul>
        </>
    )
}


export default Categories
