import { useEffect, useState } from "react"
import ServiceListeRubriques from "../services/ArticlesAPI"
import { Link } from "react-router-dom"
import img_article from "../images/article.png"

const Rubriques = () => {

    const [rubriques, setRubriques] = useState([])

    const fetchAll = async () => {
        const _rubriques = await ServiceListeRubriques.getRubriques()
        setRubriques(_rubriques)
    }

    useEffect(() => {
        fetchAll()
    }, [])

    return(
        <>
            <h1>
                Rubriques :
            </h1>
            <ul className="sans-style">
                {
                    rubriques.map(rubrique => {
                        return (
                            <div>
                                <li key={rubrique.$idRubrique}><Link to={`/rubrique/${rubrique.titre}`} role="button">{rubrique.titre}</Link></li>
                                <Link to={`/rubrique/${rubrique.titre}`} role="button"><img className="img_cafe_mini" src={img_cafe_produit} alt="cafe_produit"></img></Link>
                            </div>
                        )
                    })
                }
            </ul>
        </>
    )
}

export default Rubriques
