import ServiceListeProduits from "../services/ProduitsAPI"
import { useEffect, useState } from 'react'
import { Link } from "react-router-dom"

const Produits = () => {


    const [produits, setProduits] = useState([])

    const fetchAll = async () => {

        try {
            const _produits = await ServiceListeProduits.getProduits()
            setProduits(_produits)
            console.log(_produits);
            
        } catch (error) {
            console.log(error);
            console.log("Problème avec fetchAll des produit");
        }
    }

    useEffect(() => {
        fetchAll()
    }, [])


    return (
        <>
            <h1>
                Produits :
            </h1>
            <ul className="list-unstyled">
                {
                    produits.map(produit => {
                        return (<li key={produit.idProduit}><Link to={`/produits/${produit.idProduit}`} role="button">{produit.libelleProduit}</Link></li>)
                    })
                }
            </ul>
        </>
    )
}

export default Produits
