import ServiceListeProduits from "../services/ProduitsAPI"
import { useEffect, useState } from "react"
import { Link } from "react-router-dom"

const Categories = () => {

    const [categories, setCategories] = useState([])

    const fetchAll = async () => {
        try {
            const _categories = await ServiceListeProduits.getCategories()
            setCategories(_categories)
            console.log(_categories);
        } catch (error) {
            console.log(error);
            console.log("Problème avec fetchAll de toutes les categories");
        }
        
    }

    useEffect(() => {
        fetchAll()
    }, [])

    return (
        <>
            
            <h1>Catégories :</h1>
            <ul className="sans-style">
                {
                    categories.map(categorie => {
                        return (
                        <li key={categorie.idCategorie}><Link to={`/categorie/${categorie.idCategorie}`} role="button">{categorie.libelleCategorie}</Link></li>)
                    })
                }
            </ul>
        </>
    )
}


export default Categories
