import axiosInstance from "../config/axios"
import ServiceListeProduits from "../services/ListeProduits"
import { useEffect, useState } from 'react'
import { Link } from "react-router-dom"

const Produits = () => {


    const [produits, setProduits] = useState([])

    const fetchAll = async () => {

        try {
            const _produits = await ServiceListeProduits.getProduits()
            setProduits(_produits)
            console.log(_produits);
            
        } catch (error) {
            console.log(error);
            console.log("Problème avec fetchAll");
        }
    }

    useEffect(() => {
        fetchAll()
    }, [])


    return (
        <>
            <p>
                Produits :
            </p>
            <ul>
                {
                    produits.map(produit => {
                        return (<li key={produit.id}><Link to={`/produits/{id}`}></Link></li>)
                    })
                }
            </ul>
        </>
    )
}

export default Produits
